$(document).ready(function() {
    $('.slick-slider__list').slick({
      dots: true,
      arrows:false,
      infinite: true, 
      speed: 300,	  
      slidesToShow: 1,
      slidesToScroll: 1
    });	
    $('.reviews-slider').slick({
      dots: false,
      arrows:true,
      infinite: true, 
      speed: 300,	  
      slidesToShow: 1,
      slidesToScroll: 1
    });	
    $('.teachers-slider').slick({
      dots: false,
      arrows:true,
      infinite: true, 
      speed: 300,   
      slidesToShow: 3,
      slidesToScroll: 1,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            infinite: true, 
            slidesToShow: 1,
            slidesToScroll: 1,
          }
        }
      ],
    }); 
    $('.partners-slider').slick({
      dots: false,
      arrows:true,
      infinite: true, 
      speed: 300,   
      slidesToShow: 3,
      slidesToScroll: 1,
      responsive: [
        {
          breakpoint: 900,
          settings: {
            infinite: true, 
            slidesToShow: 1,
            slidesToScroll: 1,
          }
        }
      ],
    });
    
    $('.tmp-single__tabs-list').on('click', 'li:not(.active)', function () {
    	$(this)
    		.addClass('active').siblings().removeClass('active')
    		.closest('.tmp-single__tabs').find('.tmp-single__tabs-content').removeClass('active').eq($(this).index()).addClass('active');
    }); 
    
    $('.vacancy-block-link').on('click', function(e){
      $(this).toggleClass('open');
      $(this).next().slideToggle();
    });
    
    easydropdown.all(); 
    
    $.datepicker.setDefaults($.datepicker.regional['ru']);
    $( "#calendar" ).datepicker();
    
    
    
    $('.menu-mob-btn').on('click', function(){
      $(this).toggleClass('active');
      $(".menu-list").toggleClass('active');
    }); 
    
    $('#mmenu-icon').on('click', function(){
      $('#my-menu').toggleClass('mm-menu_opened');
      $(this).toggleClass('close');
    });
    
    
    
    
    
    
    
        var $menu = $("#my-menu").mmenu();
    
        var $icon = $("#mmenu-icon");
    
        var API = $menu.data( "mmenu" );
    
    
        $icon.on( "click", function() {
    
            API.open();
    
        });
    
    
        API.bind( "opened", function() {
    
           setTimeout(function() {
    
              $icon.addClass( "is-active" );
    
           }, 100);
    
           $icon.on( "click", function() {
    
              API.close();
    
           });
    
       });
    
       API.bind( "closed", function() {
    
          setTimeout(function() {
    
             $icon.removeClass( "is-active" );
    
          }, 100);
    
          $icon.on( "click", function() {
    
             API.open();
    
          });
    
       });
});

